import React, { Component } from 'react'
import NavBar from './containers/NavBar';
import NewOrder from './containers/NewOrder';
import Orders from './containers/Orders';
import OrderConfirmation from './components/OrderConfirmation';
import Login from './containers/Login';
import {
  BrowserRouter as Router,
  Route
} from 'react-router-dom';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import background from './images/pixur.png'
import { Redirect } from 'react-router-dom'

export default class App extends Component {

  state = {
    user: null,
    orders: [],
    result: null
  }
  sectionStyle = {
    backgroundImage: `url(${background})`,
    minHeight: '60px',

  }
  sectionStyl = {
    backgroundImage: `url(${background})`,
    minHeight: '1500px',
    overflow: 'scroll'
  }

  confirmOrder = (result) => {
    this.setState({ result: result })
    this.context.router.history.push(`/${this.state.user.id}/orderconfirmation`)
  }

  login = (user) => {
    fetch(`http://localhost:3000/api/v1/users/${user}`)
      .then(resp => {
        return resp.json()
      })
      .then(data => {
        console.log(data)
        this.setState({
          user: data
        })
      });
  }

  render() {


    return (
      <div style={this.sectionStyl}>
        {!this.state.user ? (
          <Login onLogin={this.login} />
        ) : (!this.state.user ? (
            <Router>
              <div>
                <div style={this.sectionStyle}>
                  <NavBar />
                </div>

                <Route exact path="/makePizza" render={(props) =>
                  <NewOrder user_id={this.state.user.id} confirmOrder={this.confirmOrder} />
                } />

                <Route exact path={`/${this.state.user.id}/orders`} render={(props) =>
                  <Orders user_id={this.state.user.id} />
                } />

              </div>
              {/* welcome user  */}
            </Router>) : (<OrderConfirmation user_id={this.state.user.id} result={this.state.result} />))}
        <div className="backy"> </div>
      </div>
    )
  }
};
