import React from 'react'

const Pizza = (props) => {

    const onClick = (id) => {
        props.removePizza(id)
    }
    return (
        <div>
            {props.size} pizza with {props.sauce} sauce, {props.cheese} cheese, {props.toppings.join(', ')}, {props.gourmet_toppings.join(', ')}
            <button onClick={props.onClick}>Remove</button>
        </div>
    )
}
export default Pizza
