import React from 'react'

const Cart = (props) => {

  const onClick = (event) => {
    const id = event.target.id
    props.removePizza(id)
  }

  const pizzas = this.props.pizzas.map(pizza => <Pizza pizza={pizza}/>)


    return(
        <div className="cart">
        <h1 className="cart header">Pizzas in Order</h1>
          <div className="pizzas">
            { pizzas }
          </div>
        </div>
    )
}

export default Cart
